    rawtex01.r = rawtex01.g * 0.00390625 + rawtex01.r;;
    rawtex11.r = rawtex11.g * 0.00390625 + rawtex11.r;;
    vec2 D00_10 = vec2(rawtex00.r, rawtex10.r);
    vec2 D01_11 = vec2(rawtex01.r, rawtex11.r);
